--
-- Name: alarm_id2name(bigint); Type: FUNCTION; Schema: public; Owner: lanview2
--

CREATE FUNCTION alarm_id2name(bigint) RETURNS text
    LANGUAGE plpgsql
    AS $_$
DECLARE
    rname TEXT;
BEGIN
    IF $1 IS NULL THEN
        return NULL;
    END IF;
    SELECT host_service_id2name(host_service_id) || '/' || alarm_message(host_service_id, max_status) INTO rname
        FROM alarms
        WHERE alarm_id = $1;
    IF NOT FOUND THEN
        rname := host_service_id2name(host_service_id) || '/ #' || $1 || 'not found'; 
    END IF;
    RETURN rname;
END;
$_$;


