-- Name: compare_db_version(integer, integer); Type: FUNCTION; Schema: public; Owner: lanview2
--

CREATE FUNCTION compare_db_version(major integer, minor integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    i int;
BEGIN
    i := get_int_sys_param('version_major');
    IF i = major THEN
        i := get_int_sys_param('version_minor');
        IF i = minor THEN
            RETURN 0;
        ELSIF  i < minor THEN
            RETURN 1;
        ELSE
            return -1;
        END IF;
    ELSIF  i < major THEN
        RETURN 1;
    ELSE
        return -1;
    END IF;
    
END
$$;


