CREATE FUNCTION crypt_user_password() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    IF TG_OP = 'UPDATE' THEN
        IF NEW.passwd IS NULL THEN
            NEW.passwd := OLD.passwd;
            RETURN NEW;
        ELSIF NEW.passwd = OLD.passwd THEN
            RETURN NEW;
        END IF;
    ELSE -- 'INSERT'
        IF NEW.passwd IS NULL THEN
            RETURN NEW;
        END IF;
    END IF;
    NEW.passwd := crypt(NEW.passwd, gen_salt('md5'));
    RETURN NEW;
END
$$;


