--
-- Name: min_shared(portshare, portshare); Type: FUNCTION; Schema: public; Owner: lanview2
--

CREATE FUNCTION min_shared(portshare, portshare) RETURNS portshare
    LANGUAGE plpgsql
    AS $_$
BEGIN
    IF $1 = 'NC' OR $2 = 'NC' THEN
        PERFORM error('DataWarn', -1, 'shared_cable', 'min_shared()', 'pports');
    ELSE
        IF $1 > $2 THEN
		RETURN min_shared($2, $1);
	END IF;
	IF ( $1 = $2 ) OR ( $1 = '' )
	  OR ( $1 = 'A' AND ( $2 = 'AA' OR $2 = 'AB' ) )
	  OR ( $1 = 'B' AND ( $2 = 'BA' OR $2 = 'BB' ) ) THEN
	    RAISE INFO 'min_shared(%,%) = %', $1,$2,$2;
	    RETURN $2;
	END IF;
    END IF;
    RAISE INFO 'min_shared(%,%) = NC', $1,$2;
    RETURN 'NC';
END;
$_$;


