-- Name: set_text_sys_param(text, text, text); Type: FUNCTION; Schema: public; Owner: lanview2
--

CREATE FUNCTION set_text_sys_param(pname text, txtval text, tname text DEFAULT 'text'::text) RETURNS reasons
    LANGUAGE plpgsql
    AS $$
DECLARE
    type_id bigint;
    rec sys_params;
BEGIN
    SELECT param_type_id INTO type_id FROM param_types WHERE param_type_name = tname;
    IF NOT FOUND THEN
        RETURN 'notfound';
    END IF;
    SELECT * INTO rec FROM sys_params WHERE sys_param_name = pname;
    IF NOT FOUND THEN
        RETURN 'insert';
    END IF;
    IF type_id = rec.param_type_id THEN
        IF txtval = rec.param_value THEN
            RETURN 'found';
        END IF;
        UPDATE sys_params SET param_value = txtval WHERE sys_param_name = pname;
        RETURN 'update';
    END IF;
    UPDATE sys_params SET param_value = txtval, param_type_id = type_id WHERE sys_param_name = pname;
    RETURN 'modify';
END
$$;


