CREATE FUNCTION shares_filt(shares portshare[], sh portshare) RETURNS portshare[]
    LANGUAGE plpgsql
    AS $$
DECLARE
    oshs    portshare[];
    i       bigint;
BEGIN
    FOR i IN 0 .. array_length(shares, 1) LOOP
        IF check_shared(shares[i], sh) THEN
            oshs := oshs || shares[i];
        END IF;
    END LOOP;
    RETURN oshs;
END;
$$;


