--

CREATE FUNCTION time_in_timeperiod(bigint, timestamp without time zone DEFAULT now()) RETURNS boolean
    LANGUAGE plpgsql
    AS $_$
BEGIN
    CASE $1
        WHEN  0 THEN    -- always
            RETURN TRUE;
        WHEN -1 THEN    -- never
            RETURN FALSE;
        ELSE
            RETURN 0 < COUNT(*) FROM tpows JOIN timeperiod_tpows USING (tpow_id)
                WHERE $1 = timeperiod_id AND int2dow(EXTRACT(DOW FROM $2)::integer) = dow AND $2::time >= begin_time AND $2::time < end_time;
    END CASE;
END;
$_$;


