    n1.node_id AS node_id1,
    n1.node_name AS node_name1,
    p1.port_name AS port_name1,
    p1.port_index AS port_index1,
    ((n1.node_name || ':'::text) || p1.port_name) AS port_full_name1,
    lldp_links.port_id2,
    n2.node_id AS node_id2,
    n2.node_name AS node_name2,
