     JOIN (nports p1
     JOIN patchs n1 USING (node_id)) ON ((p1.port_id = lldp_links.port_id1)))
     JOIN (nports p2
     JOIN patchs n2 USING (node_id)) ON ((p2.port_id = lldp_links.port_id2)));


