    phs_links.create_time,
    phs_links.create_user_id,
    phs_links.modify_time,
    phs_links.modify_user_id,
    phs_links.forward
   FROM ((phs_links
     JOIN (nports p1
     JOIN patchs n1 USING (node_id)) ON ((p1.port_id = phs_links.port_id1)))
     JOIN (nports p2
